  // Get a single arithmetic problem.
  // Where the seed is greater than the number of problems, it wraps to the start.
  def getProblem(problemIdx:Int, gameFold:String):Option[SortingProblem] = {
    if (gameFold == "train") {
      val idx = problemIdx % trainSet.length
      return Some(trainSet(idx))
    } else if (gameFold == "dev") {
      val idx = problemIdx % devSet.length
      return Some(devSet(idx))
    } else if (gameFold == "test") {
      val idx = problemIdx % testSet.length
      return Some(testSet(idx))
    }

    // return
    None
  }

}
